package com.jisu.emp_management.controller;import com.jisu.emp_management.service.ImgService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.http.HttpStatus;import org.springframework.http.MediaType;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import java.io.IOException;@RestController@RequestMapping("/images")public class ImageController {    @Autowired    private ImgService service;    @GetMapping("/{id}")    public ResponseEntity<?> downloadImage(@PathVariable Integer id) throws IOException {        byte[] imageData = service.getImgbyid(id);        return ResponseEntity.status(HttpStatus.OK)                .contentType(MediaType.valueOf("image/png"))                .body(imageData);    }}