package com.jisu.emp_management.controller;import java.util.List;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Controller;import org.springframework.ui.Model;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestMethod;import org.springframework.web.bind.annotation.RequestParam;import com.jisu.emp_management.model.Employee;import com.jisu.emp_management.service.EmpService;@Controllerpublic class RegistrationController {		@Autowired	private EmpService service;    @RequestMapping(path = "/regprocess",method = RequestMethod.POST)    public void regProcess(           @RequestParam("name") String name,           @RequestParam("pwd") String password,           @RequestParam("gender") String gender,           @RequestParam("email") String  email,           @RequestParam("dept") String dept,           @RequestParam("phone") String phone,           @RequestParam("location") String loc,           @RequestParam("pin") String pin    ){        System.out.println(name);        System.out.println(password);        System.out.println(gender);        System.out.println(email);        System.out.println(dept);        System.out.println(phone);        System.out.println(loc);        System.out.println(pin);    }            @RequestMapping(path = "/test",method = RequestMethod.GET)    public String getTaskform(Model model) {    	List<Employee> emps=this.service.getAllEmployee();    	model.addAttribute("emps", emps);    	    	return "test";    }        @RequestMapping(path = "/testsub",method = RequestMethod.POST)    public String submit(    		@RequestParam("sub") String subject,    		@RequestParam("emps") List<Integer> list,    		@RequestParam("desc") String desc    		) {    	    	System.out.println(subject);    	System.out.println(list);    	System.out.println();    	    		    	    	return "";    }}