package com.jisu.emp_management.service;import com.jisu.emp_management.model.AdminMsg;import com.jisu.emp_management.model.Message;import com.jisu.emp_management.repo.MassMessageRepo;import com.jisu.emp_management.repo.MessageRepo;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;@Servicepublic class AdminMsgService {    @Autowired    private MessageRepo repo;    public void saveMsg(AdminMsg msg){        this.repo.save(msg);    }    public List<AdminMsg> getAllmsg(){        return this.repo.findAll();    }    public AdminMsg getSinglemsg(Integer id){        return this.repo.findById(id).orElseThrow(()->new RuntimeException("Not found"));    }    public void deleteMsg(Integer id){        this.repo.deleteById(id);    }}